mapper.sql:
WITH RankedData AS ( SELECT cilt.unty_cntrct_no, cilt.req_no, cilt.cntrct_date, cilt.thtm_cntrct_amt, cidlt.prdct_clsfc_no, cilt.cntrct_nm, cilt.dminstt_list, cilt.cntrct_instt_nm, cilt.cntrct_cncls_mthd_nm, cilt.ntce_no, cilt.cntrct_prd, cilt.base_dtls, cilt.corp_list, ROW_NUMBER() OVER ( PARTITION BY CASE WHEN cilt.req_no = '' THEN cilt.unty_cntrct_no -- req_no가 빈 값인 경우 고유 ID로 unty_cntrct_no를 사용 ELSE cilt.req_no END ORDER BY cilt.cntrct_date ASC ) AS RowNum, RANK() OVER ( PARTITION BY CASE WHEN cilt.req_no = '' THEN cilt.unty_cntrct_no ELSE cilt.req_no END ORDER BY cilt.cntrct_date ASC ) AS CntrctCnt, RANK() OVER ( PARTITION BY CASE WHEN cilt.req_no = '' THEN cilt.unty_cntrct_no ELSE cilt.req_no END ORDER BY cilt.cntrct_date DESC ) AS ReverseRank FROM contract_info_list_thing cilt INNER JOIN contract_info_detail_list_thing cidlt ON cidlt.unty_cntrct_no = cilt.unty_cntrct_no WHERE cidlt.prdct_clsfc_no IN ('39121103','39121189','41112498','41113319','39121801','39121104','39121101','39121006','46171622','41112501','39121198','43232499','81111599','39121106','41112407','21101593','60131303','43221733','41111938','39121011','43201802','47101516')
AND cilt.dminstt_list LIKE CONCAT('%', #{dminsttNm}, '%')
AND cilt.cntrct_instt_nm LIKE CONCAT('%', #{dminsttNmDetail}, '%')
AND cidlt.prdct_clsfc_no LIKE CONCAT('%', #{prdctClsfcNo}, '%')
AND cilt.cntrct_cncls_mthd_nm LIKE CONCAT('%', #{cntctCnclsMthdNm}, '%')
AND DATE(cilt.cntrct_date) = #{firstCntrctDate}
AND YEAR(cilt.cntrct_date) = #{year}
AND DATE_FORMAT(cilt.cntrct_date, '%Y-%m') = #{month}
AND DATE_FORMAT(cilt.cntrct_date, '%Y-%m') BETWEEN #{rangeStart} AND #{rangeEnd}
)

mapper.select:
SELECT cmpNm as cmpNm, cntrctNm as cntrctNm, dminsttNm as dminsttNm, dminsttNmDetail as dminsttNmDetail, prdctClsfcNo as prdctClsfcNo, cntctCnclsMthdNm as cntctCnclsMthdNm, ntceNo as ntceNo, ifnull(date_format(firstCntrctDate, '%Y-%m-%d'),'-') as firstCntrctDate, firstCntrctAmt as firstCntrctAmt, ifnull(date_format(LastCntrctDate, '%Y-%m-%d'),'-') as cntrctDate, LastCntrctAmt as thtmCntrctAmt, LastCntrctCnt as cntrctCnt, id as id, checked as checked FROM daily_contracts_things WHERE 1=1
AND dminsttNm LIKE CONCAT('%', #{dminsttNm}, '%')
AND dminsttNmDetail LIKE CONCAT('%', #{dminsttNmDetail}, '%')
AND prdctClsfcNo LIKE CONCAT('%', #{prdctClsfcNo}, '%')
AND cntctCnclsMthdNm LIKE CONCAT('%', #{cntctCnclsMthdNm}, '%')
AND firstCntrctDate = #{firstCntrctDate}
AND YEAR(firstCntrctDate) = #{year}
AND DATE_FORMAT(firstCntrctDate, '%Y-%m') = #{month}
AND DATE_FORMAT(firstCntrctDate, '%Y-%m') BETWEEN #{rangeStart} AND #{rangeEnd}
AND checked = 1
ORDER BY firstCntrctDate DESC, id ASC LIMIT #{start}, #{length}

SELECT COUNT(*) FROM ( SELECT cmpNm as cmpNm, cntrctNm as cntrctNm, dminsttNm as dminsttNm, dminsttNmDetail as dminsttNmDetail, prdctClsfcNo as prdctClsfcNo, cntctCnclsMthdNm as cntctCnclsMthdNm, ntceNo as ntceNo, date_format(firstCntrctDate, '%Y-%m-%-d') as firstCntrctDate, firstCntrctAmt as firstCntrctAmt, date_format(LastCntrctDate, '%Y-%m-%-d') as cntrctDate, LastCntrctAmt as thtmCntrctAmt, LastCntrctCnt as cntrctCnt, id as id, checked as checked FROM daily_contracts_things WHERE 1=1
AND dminsttNm LIKE CONCAT('%', #{dminsttNm}, '%')
AND dminsttNmDetail LIKE CONCAT('%', #{dminsttNmDetail}, '%')
AND prdctClsfcNo LIKE CONCAT('%', #{prdctClsfcNo}, '%')
AND cntctCnclsMthdNm LIKE CONCAT('%', #{cntctCnclsMthdNm}, '%')
AND firstCntrctDate = #{firstCntrctDate}
AND YEAR(firstCntrctDate) = #{year}
AND DATE_FORMAT(firstCntrctDate, '%Y-%m') = #{month}
AND DATE_FORMAT(firstCntrctDate, '%Y-%m') BETWEEN #{rangeStart} AND #{rangeEnd}
AND checked = 1
ORDER BY firstCntrctDate DESC, id ASC ) as T

