mapper.update:
UPDATE daily_contracts_things
SET checked = #{checked}
WHERE id = #{id}

UPDATE daily_contracts_services
SET checked = #{checked}
WHERE id = #{id}

UPDATE daily_contracts_construction_work
SET checked = #{checked}
WHERE id = #{id}

mapper.select:
SELECT *
FROM daily_contracts_services
WHERE is_selected = 0

    AND dminsttNm = #{dminsttNm}


    AND dminsttNmDetail = #{dminsttNmDetail}


    AND prdctClsfcNo = #{prdctClsfcNo}


    AND cntctCnclsMthdNm = #{cntctCnclsMthdNm}


    AND firstCntrctDate = #{firstCntrctDate}

ORDER BY id DESC
