mapper.select:
WITH RankedData AS ( SELECT cilt.unty_cntrct_no, cilt.req_no, cilt.cntrct_date, cilt.thtm_cntrct_amt, cidlt.prdct_clsfc_no, cilt.cntrct_nm, cilt.dminstt_list, cilt.cntrct_instt_nm, cilt.cntrct_cncls_mthd_nm, cilt.ntce_no, cilt.cntrct_prd, cilt.base_dtls, cilt.corp_list, ROW_NUMBER() OVER (PARTITION BY cilt.req_no ORDER BY cilt.cntrct_date ASC) AS RowNum, RANK() OVER (PARTITION BY cilt.req_no ORDER BY cilt.cntrct_date ASC) AS CntrctCnt, RANK() OVER (PARTITION BY cilt.req_no ORDER BY cilt.cntrct_date DESC) AS ReverseRank FROM contract_info_list_thing cilt INNER JOIN contract_info_detail_list_thing cidlt ON cidlt.unty_cntrct_no = cilt.unty_cntrct_no WHERE cidlt.prdct_clsfc_no IN ('39121103','39121189','41112498','41113319','39121801','39121104','39121101','39121006','46171622','41112501','39121198','43232499','81111599','39121106','41112407','21101593','60131303','43221733','41111938','39121011','43201802','47101516')
AND cilt.dminstt_list LIKE CONCAT('%', #{dminsttNm}, '%')
AND cilt.cntrct_instt_nm LIKE CONCAT('%', #{dminsttNmDetail}, '%')
AND cidlt.prdct_clsfc_no LIKE CONCAT('%', #{prdctClsfcNo}, '%')
AND cilt.cntrct_cncls_mthd_nm LIKE CONCAT('%', #{cntctCnclsMthdNm}, '%')
AND DATE(cilt.cntrct_date) = #{firstCntrctDate}
AND YEAR(cilt.cntrct_date) = #{year}
AND DATE_FORMAT(cilt.cntrct_date, '%Y-%m') = #{month}
AND DATE_FORMAT(cilt.cntrct_date, '%Y-%m') BETWEEN #{rangeStart} AND #{rangeEnd}
GROUP BY CILT.UNTY_CNTRCT_NO ), FirstData AS ( SELECT req_no, MIN(cntrct_date) AS FirstCntrctDate, MIN(thtm_cntrct_amt) AS FirstCntrctAmt FROM RankedData GROUP BY req_no ) SELECT SUBSTRING_INDEX(SUBSTRING_INDEX(rd.corp_list, '^', 4), '^', -1) AS cmpNm, rd.cntrct_nm AS cntrctNm, SUBSTRING_INDEX(SUBSTRING_INDEX(rd.dminstt_list, '^', 3), '^', -1) AS dminsttNm, rd.cntrct_instt_nm AS dminsttNmDetail, rd.prdct_clsfc_no AS prdctClsfcNo, rd.cntrct_cncls_mthd_nm AS cntctCnclsMthdNm, rd.ntce_no AS ntceNo, date_format(fd.FirstCntrctDate, '%Y-%m-%d') AS firstCntrctDate, fd.FirstCntrctAmt AS firstCntrctAmt, date_format(rd.cntrct_date, '%Y-%m-%d') AS cntrctDate, rd.thtm_cntrct_amt AS thtmCntrctAmt, rd.CntrctCnt AS cntrctCnt, CASE WHEN rd.ReverseRank = 1 THEN 'Y' ELSE 'N' END AS finalCntrctYn, rd.unty_cntrct_no, rd.req_no, rd.cntrct_prd, rd.base_dtls, REPLACE(SUBSTRING_INDEX(SUBSTRING_INDEX(rd.corp_list, '^', 10), '^', -1), ']', '') AS cmpNo FROM RankedData rd LEFT JOIN FirstData fd ON rd.req_no = fd.req_no ORDER BY rd.req_no DESC, rd.cntrct_date DESC LIMIT #{start}, #{length}

SELECT COUNT(*) FROM ( WITH RankedData AS ( SELECT cilt.unty_cntrct_no, cilt.req_no, cilt.cntrct_date, cilt.thtm_cntrct_amt, cidlt.prdct_clsfc_no, cilt.cntrct_nm, cilt.dminstt_list, cilt.cntrct_instt_nm, cilt.cntrct_cncls_mthd_nm, cilt.ntce_no, cilt.cntrct_prd, cilt.base_dtls, cilt.corp_list, ROW_NUMBER() OVER (PARTITION BY cilt.req_no ORDER BY cilt.cntrct_date ASC) AS RowNum, RANK() OVER (PARTITION BY cilt.req_no ORDER BY cilt.cntrct_date ASC) AS CntrctCnt, RANK() OVER (PARTITION BY cilt.req_no ORDER BY cilt.cntrct_date DESC) AS ReverseRank FROM contract_info_list_thing cilt INNER JOIN contract_info_detail_list_thing cidlt ON cidlt.unty_cntrct_no = cilt.unty_cntrct_no WHERE cidlt.prdct_clsfc_no IN ('39121103','39121189','41112498','41113319','39121801','39121104','39121101','39121006','46171622','41112501','39121198','43232499','81111599','39121106','41112407','21101593','60131303','43221733','41111938','39121011','43201802','47101516')
AND cilt.dminstt_list LIKE CONCAT('%', #{dminsttNm}, '%')
AND cilt.cntrct_instt_nm LIKE CONCAT('%', #{dminsttNmDetail}, '%')
AND cidlt.prdct_clsfc_no LIKE CONCAT('%', #{prdctClsfcNo}, '%')
AND cilt.cntrct_cncls_mthd_nm LIKE CONCAT('%', #{cntctCnclsMthdNm}, '%')
AND DATE(cilt.cntrct_date) = #{firstCntrctDate}
AND YEAR(cilt.cntrct_date) = #{year}
AND DATE_FORMAT(cilt.cntrct_date, '%Y-%m') = #{month}
AND DATE_FORMAT(cilt.cntrct_date, '%Y-%m') BETWEEN #{rangeStart} AND #{rangeEnd}
GROUP BY CILT.UNTY_CNTRCT_NO ), FirstData AS ( SELECT req_no, MIN(cntrct_date) AS FirstCntrctDate, MIN(thtm_cntrct_amt) AS FirstCntrctAmt FROM RankedData GROUP BY req_no ) SELECT SUBSTRING_INDEX(SUBSTRING_INDEX(rd.corp_list, '^', 4), '^', -1) AS cmpNm, rd.cntrct_nm AS cntrctNm, SUBSTRING_INDEX(SUBSTRING_INDEX(rd.dminstt_list, '^', 3), '^', -1) AS dminsttNm, rd.cntrct_instt_nm AS dminsttNmDetail, rd.prdct_clsfc_no AS prdctClsfcNo, rd.cntrct_cncls_mthd_nm AS cntctCnclsMthdNm, rd.ntce_no AS ntceNo, date_format(fd.FirstCntrctDate, '%Y-%m-%d') AS firstCntrctDate, fd.FirstCntrctAmt AS firstCntrctAmt, date_format(rd.cntrct_date, '%Y-%m-%d') AS cntrctDate, rd.thtm_cntrct_amt AS thtmCntrctAmt, rd.CntrctCnt AS cntrctCnt, CASE WHEN rd.ReverseRank = 1 THEN 'Y' ELSE 'N' END AS finalCntrctYn, rd.unty_cntrct_no, rd.req_no, rd.cntrct_prd, rd.base_dtls, REPLACE(SUBSTRING_INDEX(SUBSTRING_INDEX(rd.corp_list, '^', 10), '^', -1), ']', '') AS cmpNo FROM RankedData rd LEFT JOIN FirstData fd ON rd.req_no = fd.req_no ORDER BY rd.req_no DESC, rd.cntrct_date DESC ) as T

SELECT unty_cntrct_no, cntrct_nm, cntrct_prd, tot_cntrct_amt, thtm_cntrct_amt, cntrct_instt_nm, base_dtls, cntrct_cncls_mthd_nm, pub_prcrmnt_clsfc_nm, corp_list FROM contract_info_service WHERE 1=1 and pub_prcrmnt_clsfc_no in ('80161599','80161590','81111599','72101597','81111899')
AND ( cntrct_nm LIKE CONCAT('%', #{search}, '%') OR cntrct_instt_nm LIKE CONCAT('%', #{search}, '%') )
LIMIT #{start}, #{length}

SELECT unty_cntrct_no, cnstwk_nm as cntrct_nm, cntrct_prd, tot_cntrct_amt, thtm_cntrct_amt, cntrct_instt_nm, base_dtls, cntrct_cncls_mthd_nm, pub_prcrmnt_clsfc_nm, corp_list FROM contract_info_construction_work WHERE 1=1 and pub_prcrmnt_clsfc_no in ('72151597','72151699','72151295','72101507')
AND ( cnstwk_nm LIKE CONCAT('%', #{search}, '%') OR cntrct_instt_nm LIKE CONCAT('%', #{search}, '%') )
LIMIT #{start}, #{length}
